function findBestPattern(request: string): WorkflowPattern {
  // „Ç≠„Éº„ÉØ„Éº„Éâ„Éû„ÉÉ„ÉÅ„É≥„Ç∞„Å´„Çà„Çã„Çπ„Ç≥„Ç¢„É™„É≥„Ç∞
  let bestMatch = patterns.faq; // „Éá„Éï„Ç©„É´„Éà
  let highestScore = 0;

  for (const [key, pattern] of Object.entries(patterns)) {
    const score = pattern.keywords.filter(keyword => 
      request.includes(keyword)
    ).length;

    if (score > highestScore) {
      highestScore = score;
      bestMatch = pattern;
    }
  }

  return bestMatch;
}

function evaluateComplexity(pattern: WorkflowPattern): 'simple' | 'medium' | 'complex' {
  const nodeCount = pattern.required_nodes.length;
  const hasConditionals = pattern.required_nodes.includes('if-else');
  const hasLoops = pattern.required_nodes.includes('iteration');
  const hasExternalAPIs = pattern.required_nodes.includes('http-request');

  if (nodeCount > 10 || hasLoops || (hasConditionals && hasExternalAPIs)) {
    return 'complex';
  } else if (nodeCount > 5 || hasConditionals || hasExternalAPIs) {
    return 'medium';
  }
  return 'simple';
}
```

