// Helper functions
function findBestPattern(request: string): WorkflowPattern {
  const lowerRequest = request.toLowerCase();
  let bestMatch = patterns.chatbot; // Default fallback
  let highestScore = 0;

  for (const [key, pattern] of Object.entries(patterns)) {
    const score = pattern.keywords.reduce((acc, keyword) => {
      return acc + (lowerRequest.includes(keyword.toLowerCase()) ? 1 : 0);
    }, 0);

    if (score > highestScore) {
      highestScore = score;
      bestMatch = pattern;
    }
  }

  return bestMatch;
}

function detectIndustry(request: string): string | undefined {
  const lowerRequest = request.toLowerCase();
  let bestMatch: string | undefined;
  let highestScore = 0;

  for (const [industry, context] of Object.entries(industryContexts)) {
    const score = context.keywords.reduce((acc, keyword) => {
      return acc + (lowerRequest.includes(keyword.toLowerCase()) ? 1 : 0);
    }, 0);

    if (score > highestScore && score > 0) {
      highestScore = score;
      bestMatch = industry;
    }
  }

  return bestMatch;
}

function generateAppName(request: string, pattern: WorkflowPattern): string {
  // Extract key nouns from the request
  const keywords = request.match(/[„ÅÅ-„Çì„Ç°-„É∂‰∏Ä-Èæ•a-zA-Z0-9]+/g) || [];

  // Common app type suffixes based on workflow type
  const suffixes: Record<string, string> = {
    simple_qa: 'Q&A„Ç∑„Çπ„ÉÜ„É†',
    advanced_chat: '„Çµ„Éù„Éº„Éà„Ç∑„Çπ„ÉÜ„É†',
    workflow: '„ÉØ„Éº„ÇØ„Éï„É≠„Éº',
    completion: 'ÁîüÊàê„ÉÑ„Éº„É´',
    agent_chat: '„ÉÅ„É£„ÉÉ„Éà„Éú„ÉÉ„Éà'
  };

  // Try to find meaningful keywords
  const meaningfulKeywords = keywords.filter(word => 
    word.length > 2 && !['‰Ωú„Çä„Åü„ÅÑ', '„Åó„Åü„ÅÑ', '„Ç∑„Çπ„ÉÜ„É†', '„Ç¢„Éó„É™'].includes(word)
  );

  if (meaningfulKeywords.length > 0) {
    return `${meaningfulKeywords[0]}${suffixes[pattern.workflow_type]}`;
  }

  return `Êñ∞Ë¶è${suffixes[pattern.workflow_type]}`;
}

function evaluateComplexity(pattern: WorkflowPattern, industryFeatures: string[] = []): 'simple' | 'medium' | 'complex' {
  const nodeCount = pattern.required_nodes.length;
  const featureCount = pattern.suggested_features.length + industryFeatures.length;
  const hasConditionals = pattern.required_nodes.includes('if-else');
  const hasLoops = pattern.required_nodes.includes('iteration') || pattern.required_nodes.includes('loop');
  const hasExternalAPIs = pattern.required_nodes.includes('http-request');
  const hasCode = pattern.required_nodes.includes('code');

  // Complex criteria
  if (nodeCount > 10 || hasLoops || (hasConditionals && hasExternalAPIs && hasCode) || featureCount > 8) {
    return 'complex';
  }

  // Medium criteria
  if (nodeCount > 5 || hasConditionals || hasExternalAPIs || hasCode || featureCount > 4) {
    return 'medium';
  }

  return 'simple';
}

function identifyMissingInfo(request: string, pattern: WorkflowPattern): string[] {
  const missingInfo: string[] = [];
  const lowerRequest = request.toLowerCase();

  // Check for common missing information based on workflow type
  const commonMissingInfo: Record<string, string[]> = {
    simple_qa: [
      { check: ['„Éá„Éº„Çø„ÇΩ„Éº„Çπ', '„Éä„É¨„ÉÉ„Ç∏„Éô„Éº„Çπ', 'ÊÉÖÂ†±Ê∫ê'], missing: 'Q&A„ÅÆ„Éá„Éº„Çø„ÇΩ„Éº„Çπ„Åæ„Åü„ÅØ„Éä„É¨„ÉÉ„Ç∏„Éô„Éº„Çπ' }
    ],
    advanced_chat: [
      { check: ['ÂØæÂøúÊôÇÈñì', 'Âñ∂Ê•≠ÊôÇÈñì'], missing: 'ÂØæÂøúÊôÇÈñìÂ∏Ø„ÅÆÊåáÂÆö' },
      { check: ['Ë®ÄË™û', 'Â§öË®ÄË™û', 'Ëã±Ë™û'], missing: 'ÂØæÂøúË®ÄË™û„ÅÆÊåáÂÆö' }
    ],
    workflow: [
      { check: ['ÂÖ•Âäõ', '„Ç§„É≥„Éó„ÉÉ„Éà', '„Éá„Éº„Çø'], missing: 'ÂÖ•Âäõ„Éá„Éº„Çø„ÅÆÂΩ¢Âºè' },
      { check: ['Âá∫Âäõ', '„Ç¢„Ç¶„Éà„Éó„ÉÉ„Éà', 'ÁµêÊûú'], missing: 'Âá∫ÂäõÂΩ¢Âºè„ÅÆÊåáÂÆö' }
    ],
    agent_chat: [
      { check: ['ÊÄßÊ†º', '„Éà„Éº„É≥', '„Ç≠„É£„É©„ÇØ„Çø„Éº'], missing: '„ÉÅ„É£„ÉÉ„Éà„Éú„ÉÉ„Éà„ÅÆÊÄßÊ†ºË®≠ÂÆö' }
    ],
    completion: [
      { check: ['„ÉÜ„É≥„Éó„É¨„Éº„Éà', '„Éï„Ç©„Éº„Éû„ÉÉ„Éà'], missing: 'ÁîüÊàê„ÉÜ„É≥„Éó„É¨„Éº„Éà„ÅÆÊåáÂÆö' }
    ]
  };

  const checks = commonMissingInfo[pattern.workflow_type] || [];

  for (const item of checks) {
    const hasInfo = item.check.some(keyword => lowerRequest.includes(keyword));
    if (!hasInfo) {
      missingInfo.push(item.missing);
    }
  }

  // Check for authentication/security requirements
  if (!lowerRequest.includes('Ë™çË®º') && !lowerRequest.includes('„É≠„Ç∞„Ç§„É≥') && !lowerRequest.includes('„Çª„Ç≠„É•„É™„ÉÜ„Ç£')) {
    missingInfo.push('Ë™çË®º„Éª„Çª„Ç≠„É•„É™„ÉÜ„Ç£Ë¶Å‰ª∂');
  }

  // Check for error handling
  if (!lowerRequest.includes('„Ç®„É©„Éº') && !lowerRequest.includes('‰æãÂ§ñ')) {
    missingInfo.push('„Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞„ÅÆÊñπÈáù');
  }

  return missingInfo;
}

// Main tool definition
export const analyzeRequestTool = createTool({
  id: 'analyze-request',
  description: 'Ëá™ÁÑ∂Ë®ÄË™û„ÅÆË¶ÅÊ±Ç„ÇíÊßãÈÄ†Âåñ„Åï„Çå„ÅüDify„ÉØ„Éº„ÇØ„Éï„É≠„ÉºË¶Å‰ª∂„Å´Â§âÊèõ',
  inputSchema: z.object({
    request: z.string().describe('„É¶„Éº„Ç∂„Éº„Åã„Çâ„ÅÆËá™ÁÑ∂Ë®ÄË™ûË¶ÅÊ±Ç'),
  }),
  outputSchema,
  execute: async ({ context }) => {
    const request = context.request;

    // 1. Find the best matching pattern
    const matchedPattern = findBestPattern(request);

    // 2. Detect industry context
    const industry = detectIndustry(request);

    // 3. Generate app name
    const appName = generateAppName(request, matchedPattern);

    // 4. Combine features
    const allFeatures = [
      ...matchedPattern.suggested_features,
      ...(industry ? industryContexts[industry].features : [])
    ];

    // 5. Evaluate complexity
    const complexity = evaluateComplexity(
      matchedPattern, 
      industry ? industryContexts[industry].features : []
    );

    // 6. Identify missing information
    const missingInfo = identifyMissingInfo(request, matchedPattern);

    return {
      app_name: appName,
      workflow_type: matchedPattern.workflow_type,
      required_nodes: matchedPattern.required_nodes,
      suggested_features: allFeatures,
      complexity_level: complexity,
      missing_info: missingInfo,
      clarifications_needed: matchedPattern.clarifications,
      industry_context: industry
    };
  },
});

