    .describe('Dify„ÉØ„Éº„ÇØ„Éï„É≠„Éº„Çø„Ç§„Éó'),
  required_nodes: z.array(z.string()).describe('ÂøÖË¶Å„Å™„Éé„Éº„Éâ„ÅÆ„É™„Çπ„Éà'),
  suggested_features: z.array(z.string()).describe('Êé®Â•®Ê©üËÉΩ'),
  complexity_level: z.enum(['simple', 'medium', 'complex']).describe('Ë§áÈõëÂ∫¶„É¨„Éô„É´'),
  missing_info: z.array(z.string()).describe('‰∏çË∂≥„Åó„Å¶„ÅÑ„ÇãÊÉÖÂ†±'),
  clarifications_needed: z.array(z.string()).optional()
    .describe('„É¶„Éº„Ç∂„Éº„Å´Á¢∫Ë™ç„Åô„Åπ„Åç‰∫ãÈ†Ö'),
  industry_context: z.string().optional()
    .describe('Ê•≠Áïå„ÉªÂàÜÈáé„ÅÆ„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà'),
});

// Workflow patterns database
const patterns: Record<string, WorkflowPattern> = {
  faq: {
    keywords: ['FAQ', 'Ë≥™ÂïèÂøúÁ≠î', 'Q&A', '„Éò„É´„Éó', '„Çà„Åè„ÅÇ„ÇãË≥™Âïè', 'Âïè„ÅÑÂêà„Çè„ÅõÂØæÂøú'],
    workflow_type: 'simple_qa',
    required_nodes: ['start', 'knowledge-retrieval', 'llm', 'answer', 'end'],
    suggested_features: ['suggested_questions', 'citation', 'feedback_collection'],
