import { createTool } from '@mastra/core/tools';
import { z } from 'zod';
import { analyzeRequestTool } from './analyze_request';
import { analyzeRequestLLMTool } from './analyze_request_llm';

// Output schema (same as other versions)
const outputSchema = z.object({
  app_name: z.string().describe('Êé®Ê∏¨„Åï„Çå„Åü„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥Âêç'),
  workflow_type: z.enum(['simple_qa', 'advanced_chat', 'workflow', 'completion', 'agent_chat'])
    .describe('Dify„ÉØ„Éº„ÇØ„Éï„É≠„Éº„Çø„Ç§„Éó'),
  required_nodes: z.array(z.string()).describe('ÂøÖË¶Å„Å™„Éé„Éº„Éâ„ÅÆ„É™„Çπ„Éà'),
  suggested_features: z.array(z.string()).describe('Êé®Â•®Ê©üËÉΩ'),
  complexity_level: z.enum(['simple', 'medium', 'complex']).describe('Ë§áÈõëÂ∫¶„É¨„Éô„É´'),
  missing_info: z.array(z.string()).describe('‰∏çË∂≥„Åó„Å¶„ÅÑ„ÇãÊÉÖÂ†±'),
  clarifications_needed: z.array(z.string()).optional()
    .describe('„É¶„Éº„Ç∂„Éº„Å´Á¢∫Ë™ç„Åô„Åπ„Åç‰∫ãÈ†Ö'),
  industry_context: z.string().optional()
    .describe('Ê•≠Áïå„ÉªÂàÜÈáé„ÅÆ„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà'),
});

// Confidence scoring for pattern-based results
function calculatePatternConfidence(request: string, patternResult: any): number {
  let confidence = 0;

  // Check if app_name is meaningful
  if (!patternResult.app_name.includes('Êñ∞Ë¶è')) {
    confidence += 0.2;
  }

  // Check if industry context was detected
  if (patternResult.industry_context) {
    confidence += 0.3;
  }

  // Check if workflow type seems appropriate
  if (patternResult.workflow_type !== 'agent_chat') { // Default fallback
    confidence += 0.2;
  }

  // Check if suggested features are present
  if (patternResult.suggested_features.length > 3) {
    confidence += 0.3;
  }

  return confidence;
}

// Merge results from pattern-based and LLM-based analysis
function mergeAnalysisResults(
  patternResult: any,
  llmResult: any,
  patternConfidence: number
): any {
  // If pattern confidence is high, prefer pattern-based for speed
  if (patternConfidence > 0.7) {
    return {
      ...patternResult,
      // Enrich with any additional insights from LLM
      clarifications_needed: [
        ...(patternResult.clarifications_needed || []),
        ...(llmResult.clarifications_needed || []).filter(
          (item: string) => !patternResult.clarifications_needed?.includes(item)
        ),
      ],
    };
  }

  // Otherwise, prefer LLM result but merge features
  return {
    ...llmResult,
    suggested_features: [
      ...new Set([
        ...llmResult.suggested_features,
        ...patternResult.suggested_features,
      ]),
    ],
    missing_info: [
      ...new Set([
        ...llmResult.missing_info,
        ...patternResult.missing_info,
      ]),
    ],
  };
}

// Hybrid analyze request tool
export const analyzeRequestHybridTool = createTool({
  id: 'analyze-request-hybrid',
  description: '„Éë„Çø„Éº„É≥„Éû„ÉÉ„ÉÅ„É≥„Ç∞„Å®LLM„ÇíÁµÑ„ÅøÂêà„Çè„Åõ„ÅüÈ´òÁ≤æÂ∫¶„Å™Ë¶ÅÊ±ÇÂàÜÊûê',
  inputSchema: z.object({
    request: z.string().describe('„É¶„Éº„Ç∂„Éº„Åã„Çâ„ÅÆËá™ÁÑ∂Ë®ÄË™ûË¶ÅÊ±Ç'),
    useLLM: z.boolean().optional().default(true).describe('LLMÂàÜÊûê„Çí‰ΩøÁî®„Åô„Çã„Åã'),
  }),
  outputSchema,
  execute: async ({ context }) => {
    // First, always run pattern-based analysis (fast)
    const patternResult = await analyzeRequestTool.execute({ context });

    // Calculate confidence in pattern-based result
    const patternConfidence = calculatePatternConfidence(context.request, patternResult);

    // If LLM is disabled or pattern confidence is very high, return pattern result
    if (!context.useLLM || patternConfidence > 0.9) {
      return patternResult;
    }

    try {
      // Run LLM-based analysis in parallel for better performance
      const llmResult = await analyzeRequestLLMTool.execute({ context });

      // Merge results intelligently
      return mergeAnalysisResults(patternResult, llmResult, patternConfidence);
    } catch (error) {
      console.error('LLM analysis failed, falling back to pattern-based result:', error);
      return patternResult;
    }
  },
});

