import { analyzeRequestTool } from './src/mastra/tools/analyze_request';
import { analyzeRequestLLMTool } from './src/mastra/tools/analyze_request_llm';
import { analyzeRequestHybridTool } from './src/mastra/tools/analyze_request_hybrid';

// Test cases for comparing different approaches
const testCases = [
  {
    name: 'Simple FAQ',
    input: 'FAQ„Éú„ÉÉ„Éà„Çí‰Ωú„Çä„Åü„ÅÑ',
  },
  {
    name: 'Complex Healthcare',
    input: 'ÁóÖÈô¢„ÅÆ‰∫àÁ¥Ñ„Ç∑„Çπ„ÉÜ„É†„Çí‰Ωú„Çä„Åü„ÅÑ„ÄÇÊÇ£ËÄÖ„ÅåÁóáÁä∂„ÇíÂÖ•Âäõ„Åó„Å¶„ÄÅÈÅ©Âàá„Å™Ë®∫ÁôÇÁßë„ÇíÊé®Ëñ¶„Åó„ÄÅÁ©∫„ÅÑ„Å¶„ÅÑ„ÇãÊôÇÈñìÂ∏Ø„Åã„Çâ‰∫àÁ¥Ñ„Åß„Åç„Çã„Çà„ÅÜ„Å´„Åó„Åü„ÅÑ',
  },
  {
    name: 'Ambiguous Request',
    input: 'AI„Çí‰Ωø„Å£„Å¶Ê•≠Âãô„ÇíÂäπÁéáÂåñ„Åó„Åü„ÅÑ',
  },
  {
    name: 'Multi-feature E-commerce',
    input: 'EC„Çµ„Ç§„Éà„ÅßÂïÜÂìÅÊé®Ëñ¶„Å®Âú®Â∫´Á¢∫Ë™ç„ÄÅÊ≥®ÊñáËøΩË∑°„Åå„Åß„Åç„Çã„Ç´„Çπ„Çø„Éû„Éº„Çµ„Éù„Éº„Éà„Ç∑„Çπ„ÉÜ„É†„ÇíÊßãÁØâ„Åó„Åü„ÅÑ',
  },
  {
    name: 'Technical Documentation',
    input: '„Éû„Éº„ÇØ„ÉÄ„Ç¶„É≥„Éï„Ç°„Ç§„É´„Åã„ÇâÊäÄË°ì„Éâ„Ç≠„É•„É°„É≥„Éà„ÇíËß£Êûê„Åó„Å¶„ÄÅAPI„É™„Éï„Ç°„É¨„É≥„Çπ„ÇíËá™ÂãïÁîüÊàê„Åô„Çã„ÉÑ„Éº„É´',
  },
];

async function compareAnalysis(testCase: { name: string; input: string }) {
  console.log(`\n${'='.repeat(80)}`);
  console.log(`üìã Test Case: ${testCase.name}`);
  console.log(`üí¨ Input: "${testCase.input}"`);
  console.log('='.repeat(80));

  try {
    // Run all three approaches
    console.log('\nüîç Running Pattern-based Analysis...');
    const startPattern = Date.now();
    const patternResult = await analyzeRequestTool.execute({
      context: { request: testCase.input }
    });
    const patternTime = Date.now() - startPattern;

    console.log('\nü§ñ Running LLM-based Analysis...');
    const startLLM = Date.now();
    const llmResult = await analyzeRequestLLMTool.execute({
      context: { request: testCase.input }
    });
    const llmTime = Date.now() - startLLM;

    console.log('\nüîÄ Running Hybrid Analysis...');
    const startHybrid = Date.now();
    const hybridResult = await analyzeRequestHybridTool.execute({
      context: { request: testCase.input, useLLM: true }
    });
    const hybridTime = Date.now() - startHybrid;

    // Compare results
    console.log('\nüìä Results Comparison:');
    console.log('-'.repeat(80));

    console.log('\n1Ô∏è‚É£ Pattern-based (Time: ' + patternTime + 'ms)');
    console.log(`   App Name: ${patternResult.app_name}`);
    console.log(`   Type: ${patternResult.workflow_type}`);
    console.log(`   Complexity: ${patternResult.complexity_level}`);
    console.log(`   Industry: ${patternResult.industry_context || 'None'}`);
    console.log(`   Features: ${patternResult.suggested_features.length} items`);
    console.log(`   Missing Info: ${patternResult.missing_info.length} items`);

    console.log('\n2Ô∏è‚É£ LLM-based (Time: ' + llmTime + 'ms)');
    console.log(`   App Name: ${llmResult.app_name}`);
    console.log(`   Type: ${llmResult.workflow_type}`);
    console.log(`   Complexity: ${llmResult.complexity_level}`);
    console.log(`   Industry: ${llmResult.industry_context || 'None'}`);
    console.log(`   Features: ${llmResult.suggested_features.length} items`);
    console.log(`   Missing Info: ${llmResult.missing_info.length} items`);

    console.log('\n3Ô∏è‚É£ Hybrid (Time: ' + hybridTime + 'ms)');
    console.log(`   App Name: ${hybridResult.app_name}`);
    console.log(`   Type: ${hybridResult.workflow_type}`);
    console.log(`   Complexity: ${hybridResult.complexity_level}`);
    console.log(`   Industry: ${hybridResult.industry_context || 'None'}`);
    console.log(`   Features: ${hybridResult.suggested_features.length} items`);
    console.log(`   Missing Info: ${hybridResult.missing_info.length} items`);

    // Detailed differences
    console.log('\nüîç Key Differences:');

    if (patternResult.workflow_type !== llmResult.workflow_type) {
      console.log(`   ‚ö†Ô∏è  Workflow Type: Pattern(${patternResult.workflow_type}) vs LLM(${llmResult.workflow_type})`);
    }

    if (patternResult.complexity_level !== llmResult.complexity_level) {
      console.log(`   ‚ö†Ô∏è  Complexity: Pattern(${patternResult.complexity_level}) vs LLM(${llmResult.complexity_level})`);
    }

    // Feature comparison
    const patternFeatures = new Set(patternResult.suggested_features);
    const llmFeatures = new Set(llmResult.suggested_features);
    const llmOnlyFeatures = [...llmFeatures].filter(f => !patternFeatures.has(f));

    if (llmOnlyFeatures.length > 0) {
      console.log(`   ‚ú® LLM-only features: ${llmOnlyFeatures.join(', ')}`);
    }

  } catch (error) {
    console.error('‚ùå Error in comparison:', error);
  }
}

async function runComparison() {
  console.log('=== Analyze Request Tools Comparison ===');
  console.log('Comparing Pattern-based vs LLM-based vs Hybrid approaches\n');

  // Check if OpenAI API key is set
  if (!process.env.OPENAI_API_KEY) {
    console.error('‚ö†Ô∏è  Warning: OPENAI_API_KEY environment variable not set');
    console.error('   LLM-based analysis will fail. Set the key to test LLM features.');
  }

  for (const testCase of testCases) {
    await compareAnalysis(testCase);
  }

  console.log('\n' + '='.repeat(80));
  console.log('Comparison Complete!');
}

// Run the comparison
runComparison().catch(console.error);

