import { z } from 'zod';
import fs from 'fs/promises';
import path from 'path';

export interface PromptVersion {
  id: string;
  version: string;
  content: string;
  description?: string;
  createdAt: Date;
  updatedAt: Date;
  tags?: string[];
  metadata?: Record<string, any>;
}

export interface PromptConfig {
  id: string;
  name: string;
  description?: string;
  versions: PromptVersion[];
  currentVersion: string;
  createdAt: Date;
  updatedAt: Date;
}

export class PromptManager {
  private prompts: Map<string, PromptConfig> = new Map();
  private storageDir: string;

  constructor(storageDir: string = './prompts') {
    this.storageDir = storageDir;
  }

  async init(): Promise<void> {
    try {
      await fs.mkdir(this.storageDir, { recursive: true });
      await this.loadPrompts();
    } catch (error) {
      console.error('Failed to initialize PromptManager:', error);
    }
  }

  private async loadPrompts(): Promise<void> {
    try {
      const files = await fs.readdir(this.storageDir);
      const promptFiles = files.filter(f => f.endsWith('.json'));

      for (const file of promptFiles) {
        const content = await fs.readFile(path.join(this.storageDir, file), 'utf-8');
        const prompt = JSON.parse(content) as PromptConfig;
        this.prompts.set(prompt.id, prompt);
      }
    } catch (error) {
      console.error('Failed to load prompts:', error);
    }
  }

  async savePrompt(prompt: PromptConfig): Promise<void> {
    const filePath = path.join(this.storageDir, `${prompt.id}.json`);
    await fs.writeFile(filePath, JSON.stringify(prompt, null, 2));
    this.prompts.set(prompt.id, prompt);
  }

  async createPrompt(
    id: string,
    name: string,
    content: string,
    description?: string
  ): Promise<PromptConfig> {
    const now = new Date();
    const version: PromptVersion = {
      id: `${id}-v1.0.0`,
      version: '1.0.0',
      content,
      description,
      createdAt: now,
      updatedAt: now,
    };

    const promptConfig: PromptConfig = {
      id,
      name,
      description,
      versions: [version],
      currentVersion: version.version,
      createdAt: now,
      updatedAt: now,
    };

    await this.savePrompt(promptConfig);
    return promptConfig;
  }

  async addVersion(
    promptId: string,
    content: string,
    version: string,
    description?: string
  ): Promise<PromptVersion> {
    const prompt = this.prompts.get(promptId);
    if (!prompt) {
      throw new Error(`Prompt with id ${promptId} not found`);
    }

    const now = new Date();
    const newVersion: PromptVersion = {
      id: `${promptId}-v${version}`,
      version,
      content,
      description,
      createdAt: now,
      updatedAt: now,
    };

    prompt.versions.push(newVersion);
    prompt.currentVersion = version;
    prompt.updatedAt = now;

    await this.savePrompt(prompt);
    return newVersion;
  }

  getPrompt(id: string, version?: string): string | null {
    const prompt = this.prompts.get(id);
    if (!prompt) return null;

    const targetVersion = version || prompt.currentVersion;
    const versionData = prompt.versions.find(v => v.version === targetVersion);

    return versionData ? versionData.content : null;
  }

  getPromptConfig(id: string): PromptConfig | null {
    return this.prompts.get(id) || null;
  }

  listPrompts(): PromptConfig[] {
    return Array.from(this.prompts.values());
  }

  async rollback(promptId: string, version: string): Promise<void> {
    const prompt = this.prompts.get(promptId);
    if (!prompt) {
      throw new Error(`Prompt with id ${promptId} not found`);
    }

    const versionExists = prompt.versions.some(v => v.version === version);
    if (!versionExists) {
      throw new Error(`Version ${version} not found for prompt ${promptId}`);
    }

    prompt.currentVersion = version;
    prompt.updatedAt = new Date();
    await this.savePrompt(prompt);
  }

  async compareVersions(
    promptId: string,
    version1: string,
    version2: string
  ): Promise<{ version1: PromptVersion; version2: PromptVersion } | null> {
    const prompt = this.prompts.get(promptId);
    if (!prompt) return null;

    const v1 = prompt.versions.find(v => v.version === version1);
    const v2 = prompt.versions.find(v => v.version === version2);

    if (!v1 || !v2) return null;

    return { version1: v1, version2: v2 };
  }
}

// Singleton instance
export const promptManager = new PromptManager();

// Central export for all prompts
export { ANALYZE_REQUEST_PROMPT, ANALYZE_REQUEST_PROMPT_VERSIONS } from './analyze-request';

// Future prompts will be exported here
// Example:
// export { TEMPLATE_GENERATION_PROMPT } from './template-generation';
// export { WORKFLOW_OPTIMIZATION_PROMPT } from './workflow-optimization';

